#' Evaluate Markov Model States
#' 
#' @param x An `uneval_state_list` object generated by 
#'   [define_state_list()].
#' @param parameters An `eval_parameters` object 
#'   generated by [eval_parameters()].
#' @param expand A tibble identifying which states
#'   should be expanded.
#'   
#' @return An `eval_states` object, a list with one 
#'   data.frame per state containing a column per state 
#'   value and a line per cycle.
#'   
#' @keywords internal
eval_state_list <- function(x, parameters, expand = NULL) {
  
  # Assinging NULLS to avoid CMD Check issues
  .state <- .limit <- state_time <- .value <- NULL
  .full_state <- .name <- .to_name_expanded <- .from_name_expanded <- NULL
  
  # Get number/names of states
  n_states <- length(x)
  state_names <- names(x)
  
  # Fill in expansion table if empty
  if(is.null(expand)) {
    expand <- tibble::tibble(
      .state = state_names,
      .full_state = state_names,
      state_time = 1,
      .expand = F,
      .limit = 1
    )
  }
  
  exp_state_names <- expand$.full_state
  
  f <- function(i) {
    obj <- discount_hack(x[[i]])
    
    # update calls to dispatch_strategy()
    obj <- dispatch_strategy_hack(obj)
    
    var_names <- names(obj)
    
    # bottleneck!
    parameters %>%
      dplyr::group_by_("state_time") %>%
      dplyr::mutate_(.dots = obj) %>%
      dplyr::ungroup() %>%
      dplyr::mutate(.state = state_names[i]) %>%
      .[c("markov_cycle", "state_time", ".state", var_names)]
    
  }
  # Evaluate and Handle expansion
  vars_df <- plyr::ldply(seq_len(n_states), f) %>%
    reshape2::melt(
      id.vars = c("markov_cycle", "state_time", ".state"),
      variable.name = ".name",
      value.name = ".value"
    ) %>%
    dplyr::left_join(expand, by = c(".state" =  ".state", "state_time" = "state_time")) %>%
    dplyr::filter(state_time <= .limit) %>%
    dplyr::mutate(
      .full_state = factor(.full_state, levels = unique(exp_state_names)),
      .name = factor(.name, levels = unique(.name))
    ) %>%
    reshape2::dcast(markov_cycle + .full_state ~ .name, value.var = ".value", fill = 0)
  
  vars_df_list <- vars_df %>%
    split(.$.full_state) %>%
    lapply(function(state) dplyr::select(state, -.full_state))
  
  res <- structure(
    vars_df_list,
    class = c("eval_state_list", class(vars_df_list))
  )
  
  # Evaluate state transition values (if present)
  state_trans_uneval <- attr(x, "transitions")
  if(!is.null(state_trans_uneval)) {
    
    # Get number/names of states
    n_states_trans <- length(state_trans_uneval)
    from_state_names <- lapply(state_trans_uneval, function(y) attr(y, "from"))
    to_state_names <- lapply(state_trans_uneval, function(y) attr(y, "to"))
    
    f_state_val <- function(i) {
      obj <- discount_hack(state_trans_uneval[[i]])
      from_states <- from_state_names[[i]]
      if(any(is.na(from_states))) from_states <- state_names
      to_states <- to_state_names[[i]]
      if(any(is.na(to_states))) to_states <- state_names
      from_states_expanded <- dplyr::filter(expand, .state %in% from_states)
      max_st <- max(expand$.limit)
      
      # update calls to dispatch_strategy()
      obj <- dispatch_strategy_hack(obj)
      
      var_names <- names(obj)
      
      # bottleneck!
      eval_params <- parameters %>%
        dplyr::mutate_(.trans_id = i, .dots = obj) %>%
        .[c("markov_cycle", "state_time", ".trans_id", var_names)]
      
      # Expand evaluated values to all relevant to/from states
      to_from_df <- expand.grid(
        .from = from_states,
        .to = to_states,
        stringsAsFactors = FALSE
      )
      tidyr::crossing(to_from_df, eval_params)
    }
    
    # Evaluate and Handle expansion
    st_var_mat <- ldply(seq_len(n_states_trans), f_state_val) %>%
      reshape2::melt(
        id.vars = c("markov_cycle", "state_time", ".trans_id", ".from", ".to"),
        variable.name = ".name",
        value.name = ".value"
      ) %>%
      dplyr::left_join(
        dplyr::transmute(
          expand,
          .state = .state,
          .from_name_expanded = .full_state,
          .limit = .limit,
          state_time = state_time
        ),
        by = c(".from" =  ".state", "state_time" = "state_time")
      ) %>%
      dplyr::mutate(.to_state_time = 1) %>%
      dplyr::left_join(
        dplyr::transmute(
          expand,
          .state = .state,
          .to_name_expanded = .full_state,
          state_time = state_time
        ),
        by = c(".to" =  ".state", ".to_state_time" = "state_time")
      ) %>%
      dplyr::filter(state_time <= .limit) %>%
      dplyr::mutate(
        .to_name_expanded = factor(.to_name_expanded, levels = unique(exp_state_names)),
        .from_name_expanded = factor(.from_name_expanded, levels = unique(exp_state_names)),
        .name = factor(.name, levels = unique(.name))
      ) %>%
      reshape2::acast(
        .from_name_expanded ~ .to_name_expanded ~ markov_cycle ~ .name,
        value.var = ".value",
        fun.aggregate = sum,
        fill = 0,
        drop = F
      )
    
    attr(res, "transitions") <- st_var_mat
  }
  
  res
}

get_state_value_names.eval_state_list <- function(x){
  names(x[[1]])[-1]
}

#' Hack to Work Around a Discounting Issue
#' 
#' This function is a hack to avoid a problem with 
#' discounting when the argument is a constant.
#' 
#' The hack consists in replacing calls to
#' `discount(x)` by `discount(x * rep(1, n()))` to
#' ensure `x` is recycled to the correct length.
#' 
#' @param .dots A state object.
#'   
#' @return A modified state object.
#'   
#' @keywords internal
discount_hack <- function(.dots) {
  f <- function (x, env) {
    if (is.atomic(x) || is.name(x)) {
      x
    } else if (is.call(x)) {
      if (discount_check(x[[1]], env)) {
        x <- pryr::standardise_call(x)
        x$time <- substitute(markov_cycle - 1)
      }
      as.call(lapply(x, f, env = env))
    } else if (is.pairlist(x)) {
      as.pairlist(lapply(x, f, env = env))
    } else {
      stop(sprintf(
        "Don't know how to handle type %s.",
        typeof(x)))
    }
  }
  
  do.call(
    structure,
    c(list(
      .Data = lapply(
        .dots,
        function(x) {
          x$expr <- f(x$expr, env = x$env)
          x
        }
      )),
      attributes(.dots)
    )
  )
}

# Ensure only heemod version of discount gets used
discount_check <- function(x, env) {
  if (identical(x, quote(discount)) ||
      identical(x, quote(heemod::discount))) {
    if (identical(x, quote(heemod::discount)) &&
        (utils::packageVersion("dplyr") <= "0.5" ||
         utils::packageVersion("lazyeval") <= "0.2")) {
      warning("Install the development version of 'lazyeval' and 'dplyr' ",
              'to avoid the error \'could not find function "n"\'.')
    }
    if (identical(environment(eval(x, envir = env)),
                  asNamespace("heemod"))) {
      TRUE
    } else {
      warning("A version of 'discount()' that is not defined by heemod was found.")
      FALSE
    }
  } else {
    FALSE
  }
}
